import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Read = () => {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get("https://6503212da0f2c1f3faeb7cab.mockapi.io/crud")
      .then((res) => {
        console.log(res.data);
        setData(res.data);
      });
  }

  function handleDelete(id){
    axios.delete(`https://6503212da0f2c1f3faeb7cab.mockapi.io/crud/${id}`).then(() => {
      getData()
    })
  }

  const setToLocalStorage = (id, name, email)=>{
    localStorage.setItem("id",id);
    localStorage.setItem("name",name);
    localStorage.setItem("email",email);
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="d-flex justify-content-between m-2">
      <h2>Read Operation</h2>
      <Link to ="/read">
      <button className="btn btn-primary">Show Data</button>
      </Link>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">/</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        {data.map((eachData) => {
          return (
            <>
              <tbody>
                <tr key={eachData}>
                  <th scope="row">{eachData.id}</th>
                  <td>{eachData.name}</td>
                  <td>{eachData.email}</td>
                  <td>
                    <Link to="/update">
                    <button  className="btn btn-success" onClick={()=>setToLocalStorage(eachData.id,eachData.name,eachData.email)}>
                      Edit
                    </button>
                    </Link>
                  </td>
                  <td>
                    <button  className="btn btn-danger" onClick={()=>handleDelete(eachData.id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
    </>
  );
};

export default Read;
