import axios from "axios";
import React, { useEffect, useState } from "react";

const Read = () => {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get("https://6503212da0f2c1f3faeb7cab.mockapi.io/crud")
      .then((res) => {
        console.log(res.data);
        setData(res.data);
      });
  }

  function handleDelete(id){
    axios.delete(`https://6503212da0f2c1f3faeb7cab.mockapi.io/crud/${id}`).then(() => {
      getData()
    })
  }
  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <h2>Read Operations</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">/</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        {data.map((eachData) => {
          return (
            <>
              <tbody>
                <tr key={eachData}>
                  <th scope="row">{eachData.id}</th>
                  <td>{eachData.name}</td>
                  <td>{eachData.email}</td>
                  <td>
                    <button type="button" className="btn btn-success">
                      Edit
                    </button>
                  </td>
                  <td>
                    <button type="button" className="btn btn-danger" onClick={()=>handleDelete(eachData.id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
    </>
  );
};

export default Read;
